const fs = require('fs');
const path = require('path');
const minidump = require('minidump');

minidump.walkStack('./pool/files/minidump/080f7621-3d14-4076-a8e1-3e7c40aefc68.dmp', [path.join('pool', 'symbols')], function(err, report) {
  if(err) console.error(err);
  fs.writeFileSync('./report.txt', report);
});